# Currently not considering the 'Setpoint brake' mode: using only 'Disabled' or 'LUT control'
def setup_door():

    # Based on the currently selected benchmark type, set the brake_enabled and LUT values
    get_benchmark_type = rospy.ServiceProxy('madrob/gui/benchmark_type', MadrobBenchmarkType)
    response = get_benchmark_type()
    benchmark_type = benchmark_types[response.benchmark_type]

    # Set door mode
    # TODO call service '/madrob/door/set_mode 0/1' - benchmark_type.brake_enabled: False 0, True 1
    
    # Reverse order of lutCCW
    lutCCW = list(reversed(benchmark_type.lut))

    # Set LUT
    if benchmark_type.brake_enabled:
        # TODO Call service '/madrob/door/set_lut 0 <benchmark_type.lut>'
        # TODO Call service '/madrob/door/set_lut 1 <lutCCW>'
        pass
    return